	<div class="main clearfix">

		<!-- COLUMN 1 -->
		<div class="threeColA1">
			<div class="contentblock">
				<h1>Registration</h1>
				<form action="{action}" method="post">
					<table class="form" cellpadding="2" cellspacing="2" border="0">
						<tr>
							<td>Email: <span>*</span></td>
							<td>{email}</td>
						</tr>
						<tr>
							<td>AIM:</td>
							<td>{aim}</td>
						</tr>
						<tr>
							<td>ICQ:</td>
							<td>{icq}</td>
						</tr>
						<tr>
							<td>Gtalk:</td>
							<td>{gtalk}</td>
						</tr>
						<tr>
							<td>MSN:</td>
							<td>{msn}</td>
						</tr>
						<tr>
							<td>Change Avatar:</td>
							<td>{avatar}</td>
						</tr>
						<tr>
							<td>Change Password:</td>
							<td>{password}</td>
						</tr>
						<tr>
							<td>Confirm New Password:</td>
							<td>{password2}</td>
						</tr>
						<tr>
							<td>Submit: <span>*</span></td>
							<td><input type="submit" value="Submit!" /></td>
						</tr>
					</table>
				</form>
			</div>
			
			<!-- Plug -->
			<div class="plug video">
				<span class="legend">* Required Field</span>
			</div>

		</div>